class JupyterModelContext:
    def __init__(self, workspace_id: str,
                 snapshot_id: str,
                 model_entrypoint: str):
        self.workspaceId = workspace_id
        self.snapshotId = snapshot_id
        self.modelEntrypoint = model_entrypoint

    def to_json(self):
        return {
            "workspaceId": self.workspaceId,
            "snapshotId": self.snapshotId,
            "entrypoint": self.modelEntrypoint
        }