import pytest

from . import config
import os
from domojupyter.ai import model, ModelTrainingInformation, ModelTask, Metric, ModelTaskType


@pytest.mark.integration
def test_model():
    model.create_model("test_model", "README.md", ["requirements.txt"],
                       training=ModelTrainingInformation({"metric": Metric("metric", 1)}, {"hyper": "param"}, "test"),
                       tasks=[ModelTask(ModelTaskType.OTHER, None, None)],
                       description="testing jupyter model from local sdk")
